class Solution {
    public int findPairs(int[] nums, int k) {

        if(k < 0)
            return 0;

        int len = nums.length;

        if(len < 2)
            return 0;

        int count = 0;

        Map<Integer, Integer> map = new HashMap<>();

        for(int num : nums)
            map.put(num, map.getOrDefault(num,0)+1);


        for(int n : map.keySet()) {
            if(k == 0) {
                if(map.get(n) >= 2) {
                    count++;
                }
            }
            else {
                if(map.containsKey(n + k))
                    count++;
            }
        }
            return count;
    }
}
