class Solution {
public:
    int findPairs(vector<int>& nums, int k) { 
        int n = nums.size();  
        set<pair<int,int>> s;
        sort(nums.begin(),nums.end(),greater<int>());
        for(int i = 0; i < n; i++){  
            int find = nums[i] - k; 
            int ldx = i+1,rdx = n-1;  
                while(ldx <= rdx){
                    int mid = ldx + (rdx-ldx)/2; 
                    if(nums[mid] == find)  
                         {s.insert({nums[i],nums[mid]}); break;} 
                    else if(nums[mid] > find) ldx = mid+1; 
                    else rdx = mid-1;
                } 
        }
        return s.size();
    }
};
