def findPairs(self, nums: List[int], k: int) -> int:
        # special case
        if k == 0:
            d = {}
            for e in nums:
                if e not in d:
                    d[e] = 1
                else:
                    d[e] += 1
            return len([v for v in d.values() if v >= 2])

        elif k < 0:
            return 0

        d = {}
        s = set()

        for e in nums:
            if e not in d:
                d[e] = 1

        for key in d.keys():
            up, down = key+k, key-k
            if up in d:
                s.add((up, key)) 
            if down in d:
                s.add((key, down))
        # print(s)
        return len(s)
