            if (i > 0 && nums[i] == nums[i - 1]) continue;
            if (helper(nums, i + 1, nums[i] + k)) res++;
        }
        return res;
    }
    private boolean helper(int[] nums, int l, int target) {
        int r = nums.length - 1;
        while (l <= r) {
            int mid = l + (r - l) / 2;
            if (nums[mid] == target) return true;
            if (nums[mid] < target) l = mid + 1;
            else r = mid - 1;
        }
        return false;
    }
}
