class Solution {
public:
    //one pointer with binary search nlogn
    int findPairs(vector<int>& nums, int k) {
        int ans=0;
        sort(nums.begin(),nums.end());
        for(int i=0;i<nums.size()-1;i++){
            if(i>0 && nums[i]==nums[i-1])continue;
            int x=nums[i];
            int l=i+1,r=nums.size()-1;
            while(l<=r){
                int m=(l+r)>>1;
                if(nums[m]-x==k){
                    ans++;
                    break;
                }
                else if(nums[m]-x>k)r=m-1;
                else l=m+1;
            }
        }
        return ans;
    }
};
