class Solution {
    public int findPairs(int[] nums, int k) {
        int res=0;
        Arrays.sort(nums);
        for(int i=0;i<nums.length;i++)
		//To make sure all pairs are unique
            if(i==0||nums[i]!=nums[i-1])
                if(search(nums,i,k)) res++;

        return res;
    }
    private boolean search(int[] nums,int index,int k){
        int st=index+1,end=nums.length-1,val=nums[index]+k;
        while(st<=end){
            int mid=st+(end-st)/2;
            if(nums[mid]==val) return true;
            else if(nums[mid]>val) end=mid-1;
            else st=mid+1;
        }
        return false;
    }
}
