class Solution {
public:
    int findPairs(vector<int>& nums, int k) {
        int n = nums.size();
        sort(nums.begin(),nums.end());
        int i = 0;
        int j = 1;
        int ans = 0;
        while(j<n){
            if(nums[j]-nums[i] <= k){
                if (nums[j]-nums[i] == k) ans++;
                do {
                   j++;
               } while(j < n && nums[j] == nums[j-1]);
            }
           else {
               do {
                   i++;
                   if (i == j) j++;
               } while(i < n && nums[i] == nums[i-1]);
            }
        }
        return ans;
    }
};
