class Solution {
public:
    int findPairs(vector<int>& nums, int k) {
        int n = nums.size();
        int res = 0;
        sort(nums.begin(), nums.end());

        for(int i = 0; i < n; ++i) {
            if(i-1 >= 0 && nums[i] == nums[i - 1]) continue;
            int x = nums[i] <= 0 ? nums[i] + k: abs(nums[i]+k);            
            if(binary_search(nums.begin() + i+1, nums.end(), x)) res++;
        }

        return res;        
