class Solution:
    def findPairs(self, nums, k: int) -> int:
        previous = {nums[0]}
        pairs = set()
        for n in nums[1:]:
            if n - k in previous:
                pairs.add((n - k, n))
            if n + k in previous:
                pairs.add((n, n + k))
            previous.add(n)
        return len(pairs)
