public class Codec {
    const string TinyUrlPrefix = @"http://tinyurl.com/";
    const string KeyCharacterSet = @"abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";   //62^6 unique keys possible.
    private Random random;
    private Dictionary<string, string> urlToKey;
    private Dictionary<string, string> keyToUrl;

    public Codec()
    {
        this.random = new Random();
        this.urlToKey = new Dictionary<string, string>();
        this.keyToUrl = new Dictionary<string, string>();
    }

    // Encodes a URL to a shortened URL
    public string encode(string longUrl) {
        string key;
        if (!this.urlToKey.TryGetValue(longUrl, out key))
        {
            while (true)    //To make sure that the generated key is unique.
            {
                StringBuilder keyBuilder = new StringBuilder();
                for (int i = 0; i < 6; i++)
                {
                    int index = this.random.Next(0, KeyCharacterSet.Length);
                    keyBuilder.Append(KeyCharacterSet[index]);
                }

                key = keyBuilder.ToString();
                if (!keyToUrl.ContainsKey(key))   //To make sure that the generated key is unique.
                {
                    this.keyToUrl[key] = longUrl;
                    this.urlToKey[longUrl] = key;
                    break;
                }
            }
        }
        return Codec.TinyUrlPrefix + key;
    }

    // Decodes a shortened URL to its original URL.
    public string decode(string shortUrl) {
        string key = shortUrl.Replace(Codec.TinyUrlPrefix, string.Empty);            
        return this.keyToUrl[key];
    }
}
