public class Codec {

    // use concurrentHashMap to take care of concurrent requests
    //https://leetcode.com/problems/encode-and-decode-tinyurl/discuss/160861/Beats-91.10-Thread-safe-Java-with-Explanations

    Map <String,String> tinyURLFwdMap = new ConcurrentHashMap <>();
    Map <String,String> tinyURLRevMap = new ConcurrentHashMap <>();
    int SHORT_URL_LEN = 6;

    // Encodes a URL to a shortened URL.
    public String encode(String longUrl) {

        //if mapping already exist, return the encoded string
		//continue otherwise
        if(tinyURLFwdMap.containsKey(longUrl)){
          return (String)tinyURLFwdMap.get(longUrl);  
        } 

        Random rand = new Random();        
        String charSet = "abcdefghijklmnopqrstuvwxyz0123456789";
        StringBuffer sb = new StringBuffer();
        String shortUrl = null;

        do {

		//create a random string of length 6 usingthe charSet and random function
        for(int i = 0 ; i < SHORT_URL_LEN; i++){            
            //rand.nextInt(36) => returns 0 - 35                     
            sb.append(charSet.charAt(rand.nextInt(36)));
        }

        shortUrl = sb.toString();
        //repeat until we find a unique short url
        }while(tinyURLRevMap.containsKey(shortUrl));

		//keep both forward and reverse mapping to facilitate dup check
        tinyURLFwdMap.put(longUrl,shortUrl);
        tinyURLRevMap.put(shortUrl,longUrl);

        return shortUrl;
    }

    // Decodes a shortened URL to its original URL.
    public String decode(String shortUrl) {
       return tinyURLRevMap.get(shortUrl);
    }
}
