```
#define CHAR_VERTEX '*'

class Solution {
class Model {
public:
    char c;
    int prob;
    Model():c(0),  prob(0) {}
    Model(int character, int probability):c(character),  prob(probability) {}
    ~Model() {}
    bool operator==(Model& node) {
        bool ret = (prob==node.prob && c==node.c) ? true : false;
        return ret;
    }
    bool operator<(Model& node) {
        bool ret = (prob < node.prob) ? true : false;
        return ret;
    }
    bool operator>(Model& node) {
        bool ret = (prob > node.prob) ? true : false;
        return ret;
    }

friend std::ostream& operator<<(std::ostream& os, const Model& node) {
    os  << "[" << node.c << "]";
    os  << "[" << node.prob << "]" << endl;
    return os;
}
};
template<typename T>
class BinNode { 
private:
public:
    T data;
    int height;
    int npl;
    BinNode<T>* parent;
    BinNode<T>* lchild;
    BinNode<T>* rchild;

    BinNode(): height(0), npl(1), parent(NULL), lchild(NULL), rchild(NULL) {}
    BinNode(const T& e, BinNode<T>* p=NULL, BinNode<T>* lc=NULL, BinNode<T>* rc=NULL, int h=0, int l=1): 
        data(e), parent(p), lchild(lc), rchild(rc), height(h), npl(l){
    }
    ~BinNode() {
    }
    BinNode<T>* insertLeftChild(const T& t);
    BinNode<T>* insertRightChild(const T& t);
    bool operator==(const BinNode<T>& node);
    bool operator<(const BinNode<T>& node);
    bool operator>(const BinNode<T>& node);
    BinNode<T>& operator=(const BinNode& node);
    bool isRoot() {
        return !parent ? true : false;
    }
    bool isLeftChild() {
        if (!isRoot()) {
            if (this == parent->lchild) return true;
            return false;
        }
        else return false;
    }
    bool isRightChild() {
        if (!isRoot()) {
            if (this == parent->rchild) return true;
            return false;
        }
        else return false;
    }
    bool isLeaf() {
        return !hasChild();        
    }
    bool hasParent()  {
        return parent ? true : false;
    }
    bool hasChild()  {
        return hasLeftChild() || hasRightChild();
    }
    bool hasBothChild();
    bool hasLeftChild() { return lchild != NULL ? true : false; }
    bool hasRightChild() { return rchild != NULL ? true : false; }
    void traverse() {
        std::queue<BinNode<T>*> queue;
        BinNode<T>* node = this;
        queue.push(node);
        std::cout << "--- TREE TOP ------" << std::endl;
        while (true) {
            if (queue.empty()) break;
            node = queue.front();
            queue.pop();
            cout << (*node);
            if (node->hasLeftChild()) queue.push(node->lchild);
            if (node->hasRightChild()) queue.push(node->rchild);
        }
        std::cout << "\n--- TREE BOTTOM ------" << std::endl;
    }

friend std::ostream& operator<<(std::ostream& os, const BinNode<T>& node) {
    os << node.data;
    return os;
}

};    
public:
    BinNode<Model>* _root;
    std::map<char, string> codes;
    // Encodes a URL to a shortened URL.
    void grow(std::list<Model*>& list) {
        std::list<BinNode<Model>*> subs; //synthetically created vertexes for growing Huffman tree
        std::list<Model*>::iterator it = list.begin();
        std::list<BinNode<Model>*>::iterator it_subs;        
        Model* l;
        Model* r;
        Model* m;
        BinNode<Model>* lchild;
        BinNode<Model>* rchild;
        BinNode<Model>* vertex;
        while (it!=list.end()) {
            lchild = NULL;
            rchild = NULL;
            vertex = NULL;
            if (std::next(it, 1) == list.end()) {
                //cout << "ending..." << endl;
                if (subs.size() != 1) { 
                    cout << "!!! error" << endl;
                    break;
                }
                l = NULL;
                r = *it;
                rchild = new BinNode<Model>(*r);
                lchild = subs.back();
                subs.pop_back();
                m = new Model(CHAR_VERTEX, lchild->data.prob+r->prob);
                vertex = new BinNode<Model>(*m);
                rchild->parent = vertex;
                lchild->parent = vertex;
                vertex->lchild = lchild;
                vertex->rchild = rchild;
                _root = vertex;
                break;
            }
            else {
                l = *it;
                it++;
                r = *it;
                m = new Model(CHAR_VERTEX, (l->prob)+(r->prob));
                for (it_subs=subs.begin(); it_subs!=subs.end();it_subs++) {
                    if (*l == (*it_subs)->data) { //a copy, not the same address
                        lchild = *it_subs;
                        subs.erase(it_subs);
                        break;
                    }
                }

                for (it_subs=subs.begin(); it_subs!=subs.end();it_subs++) {
                    if (*r == (*it_subs)->data) { 
                        rchild = *it_subs;
                        subs.erase(it_subs);
                        break;
                    } 
                }

                if (lchild == NULL) lchild = new BinNode(*l);
                if (rchild == NULL) rchild = new BinNode(*r);
                vertex = new BinNode(*m);
                rchild->parent = vertex;
                lchild->parent = vertex;
                vertex->lchild = lchild;
                vertex->rchild = rchild;
                subs.push_back(vertex);
                for (std::list<Model*>::iterator itt=it; it!=list.end(); itt++) {
                    if (*m < **itt || *m == **itt) {
                        list.insert(itt, m);
                        break;
                    }
                    else if (std::next(itt, 1) == list.end()) {
                        list.push_back(m);
                        break;
                    }
                }
                it++;
            }

        }
    }
    void generate() {
        std::queue<BinNode<Model>*> queue;
        BinNode<Model>* node = _root;
        BinNode<Model>* tmp;
        queue.push(node);
        std::string code;

        while (true) {
            if (queue.empty()) break;
            node = queue.front();
            queue.pop();
            if (node->data.c == CHAR_VERTEX) {

            }
            else {
                if (node->isLeaf()) code.clear();
                tmp = node;
                while (tmp) {
                    if (tmp->isLeftChild()) code.insert(0, "0");
                    else if (tmp->isRightChild()) code.insert(0, "1");
                    tmp = tmp->parent;
                }
                if (node->data.c != CHAR_VERTEX) codes[node->data.c] = code;
            }
            if (node->hasLeftChild()) queue.push(node->lchild);
            if (node->hasRightChild()) queue.push(node->rchild);
        }

    }
    string encode(string longUrl) {
        std::map<char,int> probs;
        std::list<Model*> table;
        std::string encoded;
        for (string::iterator it=longUrl.begin(); it!=longUrl.end(); it++) {
            probs[*it]++;
        }
        for (std::map<char,int>::iterator it=probs.begin(); it!=probs.end(); it++) {
            Model* m = new Model(it->first, it->second);
            table.push_back(m);
        }
        struct comp {
            bool operator() (Model* m1, Model* m2) {
                return *m1 < *m2;
            }
        };
        //cout << "AFTER SORT" << endl;
        struct comp functor;
        table.sort(functor);
        //for (std::list<Model*>::iterator it=table.begin(); it!=table.end(); it++) cout << **it;
        grow(table);
        //cout << "AFTER GROW" << endl;
        //_root->traverse();
        generate();
        //cout << "AFTER GENERATE" << endl;
        //for (std::map<char,string>::iterator it=codes.begin(); it!=codes.end(); it++) 
        //  cout << it->first << " => " << it->second << endl; 

        for (string::iterator it=longUrl.begin(); it!=longUrl.end(); it++) {
            char c = *it;
            string code = codes[c];
            encoded.append(code);
        }
        return encoded;
    }

    // Decodes a shortened URL to its original URL.
    string decode(string shortUrl) {
        string decoded;
        string str;
        for (string::iterator it=shortUrl.begin(); it!=shortUrl.end(); it++) {
            char c = *it;
            str += c;
            for (std::map<char,string>::iterator it=codes.begin(); it!=codes.end(); it++) {
                if (!str.compare(it->second)) {
                    char c = it->first;
                    decoded.insert(decoded.end(), c);
                    str.clear();
                    break;
                }
            }   
        }
        return decoded;
    }
};

