    // Encodes a URL to a shortened URL.
    void grow(std::list<Model*>& list) {
        std::list<BinNode<Model>*> subs; //synthetically created vertexes for growing Huffman tree
        std::list<Model*>::iterator it = list.begin();
        std::list<BinNode<Model>*>::iterator it_subs;        
        Model* l;
        Model* r;
        Model* m;
        BinNode<Model>* lchild;
        BinNode<Model>* rchild;
        BinNode<Model>* vertex;
        while (it!=list.end()) {
            lchild = NULL;
            rchild = NULL;
            vertex = NULL;
            if (std::next(it, 1) == list.end()) {
                //cout << "ending..." << endl;
                if (subs.size() != 1) { 
                    cout << "!!! error" << endl;
                    break;
                }
                l = NULL;
                r = *it;
                rchild = new BinNode<Model>(*r);
                lchild = subs.back();
                subs.pop_back();
                m = new Model(CHAR_VERTEX, lchild->data.prob+r->prob);
                vertex = new BinNode<Model>(*m);
                rchild->parent = vertex;
                lchild->parent = vertex;
                vertex->lchild = lchild;
                vertex->rchild = rchild;
                _root = vertex;
                break;
            }
            else {
                l = *it;
                it++;
                r = *it;
                m = new Model(CHAR_VERTEX, (l->prob)+(r->prob));
                for (it_subs=subs.begin(); it_subs!=subs.end();it_subs++) {
                    if (*l == (*it_subs)->data) { //a copy, not the same address
                        lchild = *it_subs;
                        subs.erase(it_subs);
                        break;
                    }
                }

                for (it_subs=subs.begin(); it_subs!=subs.end();it_subs++) {
                    if (*r == (*it_subs)->data) { 
                        rchild = *it_subs;
                        subs.erase(it_subs);
                        break;
                    } 
                }

                if (lchild == NULL) lchild = new BinNode(*l);
                if (rchild == NULL) rchild = new BinNode(*r);
                vertex = new BinNode(*m);
                rchild->parent = vertex;
                lchild->parent = vertex;
                vertex->lchild = lchild;
                vertex->rchild = rchild;
                subs.push_back(vertex);
                for (std::list<Model*>::iterator itt=it; it!=list.end(); itt++) {
                    if (*m < **itt || *m == **itt) {
                        list.insert(itt, m);
                        break;
                    }
                    else if (std::next(itt, 1) == list.end()) {
                        list.push_back(m);
                        break;
                    }
                }
                it++;
            }

        }
