                    else if (tmp->isRightChild()) code.insert(0, "1");
                    tmp = tmp->parent;
                }
                if (node->data.c != CHAR_VERTEX) codes[node->data.c] = code;
            }
            if (node->hasLeftChild()) queue.push(node->lchild);
            if (node->hasRightChild()) queue.push(node->rchild);
        }

