        for (string::iterator it=longUrl.begin(); it!=longUrl.end(); it++) {
            char c = *it;
            string code = codes[c];
            encoded.append(code);
        }
        return encoded;
    }

    // Decodes a shortened URL to its original URL.
    string decode(string shortUrl) {
        string decoded;
        string str;
        for (string::iterator it=shortUrl.begin(); it!=shortUrl.end(); it++) {
            char c = *it;
            str += c;
            for (std::map<char,string>::iterator it=codes.begin(); it!=codes.end(); it++) {
                if (!str.compare(it->second)) {
                    char c = it->first;
                    decoded.insert(decoded.end(), c);
                    str.clear();
                    break;
                }
            }   
        }
        return decoded;
    }
};

