class Solution {
public:

    // Encodes a URL to a shortened URL.
    string encode(string longUrl) {
        stringstream ss;
        ss << std::hex << hash(longUrl);
        string hv = ss.str();

        if( lookup.find(ss.str()) != lookup.end()  ) {
            lookup[hv].push_back(longUrl);
            ss << "-" << lookup[hv].size()-1;
        } else {
            lookup[hv].push_back(longUrl);
            ss << "-" << "0";
        }
        return ss.str();
    }

    // Decodes a shortened URL to its original URL.
    string decode(string shortUrl) {
        string hv="";
        int id = 0;
        if(shortUrl.rfind("-") != string::npos) {
            stringstream ss;
            ss << shortUrl.substr( shortUrl.rfind("-")+1);;
            ss >> id;
            hv = shortUrl.substr(0, shortUrl.rfind("-"));
            if(lookup.find(hv) != lookup.end() ) {
                if(id < lookup[hv].size() ) 
                    return lookup[hv][id];

            }
        }
        return "";
    }
private:
    unordered_map<string,vector<string> > lookup;

    int hash(std::string const & key) {
	int hashVal = 0, len = key.length();
	for(int i=0; i<len; i++) {
		hashVal += key[i];	}
	return hashVal;
}
};

