class Solution {
public:

    // function for finding the real part of a complex number

    int find_real(string num)
    {
        int real = 1;

        char sign = '+';

        string str = "";

        int i = 0;

        if(num[0] == '-')
        {
            sign = '-';

            i++;
        }

        while(i < num.size() && isdigit(num[i]))
        {
            str += num[i];

            i++;
        }

        real= stoi(str);

        if(sign == '-')
        {
            real = -real;
        }

        return real;
    }

    // function for finding the imaginary part of a complex number

    int find_img(string num)
    {
        int img = 1;

        char sign = '+';

        string str = "";

        int i = num.size() - 2;

        while(i >= 0 && isdigit(num[i]))
        {
            str = num[i] + str;

            i--;
        }

        if(num[i] == '-')
        {
            sign = '-';
        }

        img = stoi(str);

        if(sign == '-')
        {
            img = -img;
        }

        return img;
    }

    string complexNumberMultiply(string num1, string num2) {

        // find the real and img part of num1

        int a1 = find_real(num1);

        int b1 = find_img(num1);

        // find the real and img part of num2

        int a2 = find_real(num2);

        int b2 = find_img(num2);

        // find the real part after multiplication

        int real = (a1 * a2 - b1 * b2);

        // find the img part after multiplication

        int img = (a1 * b2 + a2 * b1);

        // form res

        string res = "";

        res += to_string(real);

        res += "+";

        res += to_string(img);

        res += "i";

        return res;
    }
};
```

