class Solution {
public:
    string complexNumberMultiply(string num1, string num2) {
        int real1, real2, img1, img2;
        string s;
        real1 = stoi(num1);             // Stores number before '+'. i.e. real part
        real2 = stoi(num2);

        while(num1[0] != '+') {         // Erases the real part in the string num1
            num1.erase(0, 1);
        }
        num1.erase(0, 1);
        img1 = stoi(num1);              // Stores number after '+'. i.e. imaginary part

        while(num2[0] != '+') {         // Erases the real part in the string num2
            num2.erase(0, 1);
        }
        num2.erase(0, 1);
        img2 = stoi(num2);              // Stores number after '+'. i.e. imaginary part

        int real = (real1*real2)+(-1*(img1*img2));      // Apply the multiplication formula
        int img = (real1*img2)+(real2*img1);

        s += to_string(real);           // Convert the answer to string
        s += '+';
        s += to_string(img);
        s += 'i';

        return s;
    }
};
