class Solution {
    public String complexNumberMultiply(String num1, String num2) {

    int a = convert(0, num1.indexOf('+'), num1);


    int b = convert(num1.indexOf('+') + 1, num1.length() - 1, num1);

    int c = convert(0, num2.indexOf('+'), num2);

    int d = convert(num2.indexOf('+') + 1, num2.length() - 1, num2);




    return "" + (a * c - b * d) + "+" + (b * c + a * d) + "i";

    }

public static int convert(int i, int j, String s)
  {
    int sign = 1;

    if (s.charAt(i) == '-')
    {
      sign = -1;

      i++;
    }

    int n = 0;
    while ( i < j )
    {
      n = n * 10 + (s.charAt(i) - '0');
      i++;
    }

    return sign * n;
  }
}

