class Solution {
    class ComplexNumber {
        int real = 0;
        int imaginary = 0;

        public ComplexNumber(int r, int i) {
            this.real = r;
            this.imaginary = i;
        }

        public ComplexNumber(String s) {
            int i = 0;
            int sign = s.charAt(i) == '-' ? -1 : 1;
            if(sign == -1) {
                i++;   
            }
            while(s.charAt(i) != '+') {
                real = real * 10 + (int)(s.charAt(i)-'0');
                i++;
            }
            real *= sign;
            sign = 0;
            i++;
            sign = s.charAt(i) == '-' ? -1 : 1;
            if(sign == -1) {
                i++;   
            }
            while(s.charAt(i) != 'i') {
                imaginary = imaginary * 10 + (int)(s.charAt(i) -'0');
                i++;
            }
            imaginary *= sign;
        }

        public ComplexNumber multiply(ComplexNumber c) {
            int newReal =  this.real * c.real;
            int newImaginary = this.real * c.imaginary + this.imaginary * c.real;
            newReal += (-1 * this.imaginary * c.imaginary);
            return new ComplexNumber(newReal, newImaginary);
        }

        @Override
        public String toString() {
            StringBuilder sb = new StringBuilder();
            /*if(real < 0) {
                sb.append("-");   
            }*/
            sb.append(real);
            sb.append("+");
            /*if(imaginary < 0) {
                sb.append("-");
            }*/
            sb.append(imaginary);
            sb.append("i");
            return sb.toString();
        }   
    }


    public String complexNumberMultiply(String num1, String num2) {
        ComplexNumber c1 = new ComplexNumber(num1);
        ComplexNumber c2 = new ComplexNumber(num2);
        return c1.multiply(c2).toString();
    }


}
