    string complexNumberMultiply(string num1, string num2) {
        // variables to store the real and imaginary parts of num1
        int r1 = 0;
        int i1 = 0;

        // temp variable to store the current number
        int curr = 0;
        // temp variable helps to determine whether the current number is
        // -ve or not
        bool isneg = false;

        // Parse num1 string and retrieve the real and imaginary numbers
        for (char& ch : num1) {
            // Check if the current char is either i or +
            if (ch == '+' || ch == 'i') {
                // Check if needs to be a -ve number
                curr = isneg ? -curr : curr;

                // If the current char is '+', then the current number
                // extracted is a real number else imaginary
                (ch == '+' ? r1 : i1) = curr;
                curr = 0;
                isneg = false;
            }
            else if (ch == '-') {
                // Set the flag
                isneg = true;
            }
            else {
                // Append to the current number
                curr = curr * 10 + (ch - '0');
            }
        }

        // variables to store the real and imaginary parts of num2
        int r2 = 0;
        int i2 = 0;

        // Reset the curr number, isneg
        curr = 0;
        isneg = false;

        // Parse num2 string and retrieve the real and imaginary numbers
        for (char& ch : num2) {
            // Check if the current char is either i or +
            if (ch == '+' || ch == 'i') {
                // Check if needs to be a -ve number
                curr = isneg ? -curr : curr;

                // If the current char is '+', then the current number
                // extracted is a real number else imaginary
                (ch == '+' ? r2 : i2) = curr;
                curr = 0;
                isneg = false;
            }
            else if (ch == '-') {
                // Set the flag
                isneg = true;
            }
            else {
                // Append to the current number
                curr = curr * 10 + (ch - '0');
            }
        }

        // Perform the complex multiplication
        return to_string(r1 * r2 - i1 * i2) + "+" + to_string(r1 * i2 + r2 * i1) + "i";
    }

