class Solution {
public:
    string complexNumberMultiply(string a, string b) {
        string ar(a.begin(), a.begin() + a.find('+')); // real a
        string br(b.begin(), b.begin() + b.find('+')); // real b
        string ai, bi; // imaginary a, b

        int s = a.find('+');
        if (a[s+1] == '-') s++;
        ai.assign(a.begin() + s, a.end() - 1);
        s = b.find('+');
        if (b[s+1] == '-') s++;
        bi.assign(b.begin() + s, b.end() - 1);

        int real = stoi(ar) * stoi(br) + stoi(ai) * stoi(bi) * -1;
        int imaginary = stoi(ai) * stoi(br) + stoi(bi) * stoi(ar);
        return to_string(real) + "+" + to_string(imaginary) + "i";
    }
};
