class Solution {
    public String complexNumberMultiply(String a, String b) {
        return new Complex(a).mult(new Complex(b)).toStringFormat();
    }

    class Complex {
        int a;
        int b;

        Complex(String v) {
            String[] s = v.split("\\+");
            this.a = Integer.parseInt(s[0]);
            this.b = Integer.parseInt(s[1].substring(0, s[1].length() - 1));
        }

        Complex(int a, int b) {
            this.a = a;
            this.b = b;
        }

        Complex mult(Complex c) {
            int a1 = a * c.a;
            int b1 = a * c.b;
            int b2 = b * c.a;
            int a2 = -(b * c.b);

            return new Complex(a1 + a2, b1 + b2);
        }

        String toStringFormat() {
            return String.format("%d+%di", a, b);
        }
    }
}
