impl Solution {
    pub fn complex_number_multiply(a: String, b: String) -> String {
        let a_num = a.trim_end_matches("i").split('+').collect::<Vec<&str>>();
        let b_num = b.trim_end_matches("i").split('+').collect::<Vec<&str>>();

        let a_real = a_num[0].parse::<i32>().unwrap();
        let a_img = a_num[1].parse::<i32>().unwrap();
        let b_real = b_num[0].parse::<i32>().unwrap();
        let b_img = b_num[1].parse::<i32>().unwrap();

        return format!("{}+{}i", (a_real * b_real - a_img * b_img), (a_real * b_img + a_img * b_real));
    }
}
