var complexNumberMultiply = function(num1, num2) {
    const [a1, a2] = num1.split('+')
    const [b1, b2] = num2.split('+')

    let first = a1 * b1
    let second = a1 * b2.slice(0, -1)
    let third = a2.slice(0, -1) * b1
    let fourth = -a2.slice(0, -1) * b2.slice(0, -1)

    return `${first + fourth}+${second + third}i`
};
