class Solution {
public:
    int findNum(string str){
        int  num = 0 ;
        bool pos = true; 
        for(int i = 0 ; i < str.size() ; i++){
            if(i == 0 && (str[i] == '-' || str[i]== '+')){
                if(str[i] == '-') pos = false;
            }else{
                num = num*10 + (str[i]-'0') ;
            }
        }
        return (pos)?num:-1*num ;
    }
    string complexNumberMultiply(string num1, string num2) {
        int i = 0 , j = 0 , n1 = num1.size(), n2 = num2.size(), r1 = 0 , i1 = 0 , r2 = 0 , i2 = 0 ;
        while(i<num1.size()){
            if(num1[i] == '+'){
                r1 = findNum(num1.substr(0,i)) ;
                i1 = findNum(num1.substr(i+1, n1-i-2)) ;
                break;
            }
            i++;
        }
        while(j<num2.size()){
            if(num2[j] == '+'){
                r2 = findNum(num2.substr(0,j)) ;
                i2 = findNum(num2.substr(j+1, n2-j-2)) ;
                break;
            }
            j++;
        }
        int real = (r1*r2)-(i1*i2) ;
        int img  = (r1*i2)+(r2*i1)  ;
        return ((real<0)?"-":"") + to_string(abs(real)) +"+"+ ((img<0)?"-":"") +to_string(abs(img)) +"i" ;
    }
};
