class Solution {
public:
    string complexNumberMultiply(string num1, string num2) {
        stringstream ss1(num1);
        stringstream ss2(num2);
        string temp;
        int fr;
        int fi;
        int sr;
        int si;
        while(getline(ss1,temp,'+'))
        {
            if(temp.back()!='i')
            fr = stoi(temp);
            else
            fi = stoi(temp.substr(0,temp.length()-1));
        }

        while(getline(ss2,temp,'+'))
        {
            if(temp.back()!='i')
            sr = stoi(temp);
            else
            si = stoi(temp.substr(0,temp.length()-1));
        }

        int real = (fr*sr)-(fi*si);
        int imag = (fr*si)+(sr*fi);
        string ans = "";
        ans+=to_string(real);
        ans+="+";
        ans+=to_string(imag);
        ans+="i";
        return ans;
    }
};
```

