**SUBSETS** 
https://leetcode.com/problems/subsets
```
class Solution {
public:
    vector<vector<int>> result;

    void powerset(vector<int> nums, int curr, vector<int> res, int n){
        if(curr == n){
            result.push_back(res);
            return;
        }
        powerset(nums, curr+1, res, n);
        res.push_back(nums[curr]);
        powerset(nums, curr+1, res, n);
    }
    vector<vector<int>> subsets(vector<int>& nums) {
        int n = nums.size();
        vector<int> res;
        powerset(nums, 0, res, n);
        return result;
    }
};
```

**SUBSETS II**
https://leetcode.com/problems/subsets-ii/
```
class Solution {
public:
    vector<vector<int>> result;

    void subs(vector<int> &nums, int curr, vector<int> &res){
        result.push_back(res);
        for(int i = curr; i < nums.size(); i++){
            if(i!=curr && nums[i]==nums[i-1]){
                continue;
            }
            res.push_back(nums[i]);
            subs(nums, i+1, res);
            res.pop_back();

        }
    }
    vector<vector<int>> subsetsWithDup(vector<int>& nums) {
        if(nums.size() == 0){
            return result;
        }
        vector<int> res;
        sort(nums.begin(), nums.end());
        subs(nums, 0, res);
        return result;
    }
};
```
