/*

    Time complexity : O(2^N*K), For every index, we make 2 recursion calls and there are N elements and for
    every subset we are checking duplicates in output and in worst case it can contain K subset. so total time
    complexity is O(2^N*K).

    Space complexity : O(N*K), In worst case there can be N*K subsets and for every subset, we need O(N) 
    space on average so total space complexity is O(N*K).

    Solved using Array + BackTracking.

    Note : This approach will give TLE.

*/


/***************************************** Approach 1 *****************************************/

class Solution {
private: 
    void combinationSum2(vector<int>& candidates, int target, vector<vector<int>> &output, vector<int> &temp, int index){
        if(index == candidates.size()){
            if(target == 0){
                bool isPresent = find(output.begin(), output.end(), temp) != output.end();
                if(!isPresent){
                    output.push_back(temp);
                }
            }
            return;
        }
        if(candidates[index] <= target){
            temp.push_back(candidates[index]);
            combinationSum2(candidates, target-candidates[index], output, temp, index+1);
            temp.pop_back();
        }
        combinationSum2(candidates, target, output, temp, index+1);
    }
public:
    vector<vector<int>> combinationSum2(vector<int>& candidates, int target) {
        sort(candidates.begin(), candidates.end());
        vector<vector<int>> output;
        vector<int> temp;
        combinationSum2(candidates, target, output, temp, 0);
        return output;
    }
};






