//Motivation: such a DP solution feels possible, but turns out you have to be careful to get it working just right
//O(exp) time and memory unavoidable due to nature of the problem: asking for a list of all combinations
class Solution {
public:
    vector<vector<int>> combinationSum2(const vector<int> &candidates,const int target){
    	map<int,int> Numbers;
    	for(const int i:candidates){
    		++Numbers[i];//The purpose of regrouping duplicate elements is explained in the innermost loop
    	}
        vector<vector<vector<int>>> Ways(target+1);// Ways of forming the numbers 0,1,2,3...
        Ways[0].resize(1);//1 way to obtain 0: vector<int>{}
        for(const pair<int,int> &p:Numbers){
        	const int n{p.first};
        	for(int partial{target-n};partial>=0;--partial){//This loop must be reversed because we can only use each number once
                //In the inner loop we modify Ways[idx] with idx>partial
                //Example: If we iterate partial:0->target with n=1 we would add {1} to Ways[1], then we would add {1,1} to Ways[2] in the next iteration of the loop etc...
    			for(vector<int> V:Ways[partial]){
    				int sum{partial};
    				for(int i=0;i<p.second && sum<=target-n;++i){//We have to handle duplicate numbers in this way to avoid creating duplicate combinations
                        //Example: [10,1,2,7,6,1,5] 8
                        //If we had not regrouped the two 1s we would have created a {1,7} and a {7,1}
                        //Maybe we could sort the numbers first?
                        //[1,1,2,5,6,7,10] 8
                        //Just creates {1,7} and {1,7}. Does not solve the problem
                        //Maybe there is another way of dealing with this without the map?
    					sum+=n;
    					V.push_back(n);
        				Ways[sum].push_back(V);
    				}
        		}
        	}
        }
        return Ways[target];
    }
};
