    func(n - v[j], v, j + 1);
    vec.pop_back();
    while (j + 1 < v.size() && v[j] == v[j + 1]) j++; // skip duplicates
}

}
    vector<vector<int>> combinationSum2(vector<int>& candidates, int target) {

     sort(candidates.begin(),candidates.end());
func(target,candidates,0);



        return ans;
    }
};
