    void PrintSubset(vector<int>&candidates,int n, int target,vector<vector<int>> &ans,vector<int>temp)
    {
        if(target==0)
        {
            sort(temp.begin(),temp.end());
            if(s.find(temp)==s.end())
            {
                ans.push_back(temp);
                s.insert(temp);
            }
            return;
        }
        if(n==0)
            return;
        if(target<candidates[n-1])
            PrintSubset(candidates,n-1,target,ans,temp);
        else
