            if(remain-candidates[i]<0) break; //This line of code can reduce 7ms from execution time!
            tmp.add(candidates[i]);
            entered=candidates[i];
            ch(candidates,remain-candidates[i],i+1,tmp,ans);
            tmp.remove(tmp.size()-1); 
        }   
    }
}

