            if (candidates[i] == candidates[i - 1]) {
                nWm[idx].second++;
            } else {
                nWm.push_back({candidates[i], 1});
                idx++;
            }
        }
        n=nWm.size();

        vector<vector<vector<vector<int>>>> 
        dp(n, vector<vector<vector<int>>>(target + 1));

        return dfs(0, target, nWm, dp);
    } 
};
