     public void solve(int[] candidates,int i,List<List<Integer>>ans,List<Integer> output, int target){
	 //base case
        if(i==candidates.length){
            if(target==0){
                ans.add(new ArrayList<>(output));
            }
            return;
        }
        //include the element and since each candidate can be used one send i+1 to the function
         if(candidates[i]<=target){
            output.add(candidates[i]);
            solve(candidates,i+1,ans,output,target-candidates[i]);
            output.remove(output.size()-1);
         }
        //exclude if the number is similar to the previous as it will result in same path
        while(i+1<candidates.length && candidates[i]==candidates[i+1]){
            i++;
        }
        solve(candidates,i+1,ans,output,target);
    }
}
