    public List<List<Integer>> combinationSum2(int[] candidates, int target) {
        Map<Integer, Set<List<Integer>>> map = new HashMap<>();
        Arrays.sort(candidates);
        for (int cand : candidates) {
            if (cand > target) continue;
            for (int num = target - cand; num >= 0; num--) {
                Set<List<Integer>> tmp = map.getOrDefault(num + cand, new HashSet<>());
                Set<List<Integer>> prev = map.getOrDefault(num, new HashSet<>());
                for (List<Integer> l : prev) {
                    List<Integer> newList = new ArrayList<>();
                    newList.addAll(l);
                    newList.add(cand);
                    tmp.add(newList);
                }
                map.put(num + cand, tmp);
            }
            map.computeIfAbsent(cand, g -> new HashSet<>());
            map.get(cand).add(List.of(cand));
        }
        List<List<Integer>> res = new ArrayList<>();
        res.addAll(map.getOrDefault(target, new HashSet<>()));
        return res;
    }
