class Solution {
public:
    vector<vector<int>> res;
    set<vector<int>> s;
    void backtrack(vector<int> &nums,int target,int idx,vector<int> &v)
    {
        if(target==0)
        {
        res.push_back(v);
        s.insert(v);
        return ;
        }
        if(target<0||idx>=nums.size())
        {
            return ;
        }

        v.push_back(nums[idx]);
        backtrack(nums,target-nums[idx],idx+1,v);
        v.pop_back();

        int temp=nums[idx];
        while(idx<nums.size()&&temp==nums[idx])
            idx++;

        backtrack(nums,target,idx,v);
    }
    vector<vector<int>> combinationSum2(vector<int>& candidates, int target) 
    {
        sort(candidates.begin(),candidates.end());
        vector<int> v;
        backtrack(candidates,target,0,v);
        return res;
    }
};
