class Solution {
public:
    vector<vector<int>> ans,k;
    int target;
    void util(vector<int> c,vector<int> a,int sum, int i)
    {
        if(sum>target )
                return;
        if(sum==target)
            {
                ans.push_back(a);
                return;
            }
        for(int j=i;j<c.size();j++)
        {
            if(j>i && c[j]==c[i])
                continue;
            sum+=c[j];
            a.push_back(c[j]);
            util(c,a,sum,j+1);
            a.pop_back();
            sum-=c[j];
        }
    }

    vector<vector<int>> combinationSum2(vector<int>& c, int t) {
        target=t;
        vector<int> a;
        sort(c.begin(),c.end());
        util(c,a,0,0);
        set<vector<int>> s;
        for(auto t: ans)
        {
            if(s.find(t)==s.end())
            {
                s.insert(t);
                k.push_back(t);
            }
        }
        return k;
    }
};

