class Solution {
    int[] nums;
    boolean[] visited;
    public List<List<Integer>> permuteUnique(int[] nums) {
        this.nums = nums;
        visited = new boolean[nums.length];
        Arrays.sort(nums);
        return permuteUniqueDfs(0);
    }

    private List<List<Integer>> permuteUniqueDfs(int pos) {
        if(pos == nums.length) {
            List<List<Integer>> res = new ArrayList<>();
            List<Integer> item = new ArrayList<>();
            res.add(item);
            return res;
        }

        List<List<Integer>> res = new ArrayList<>();

        for (int i = 0; i < nums.length; i++) {
            if(visited[i]) continue;
            if(i > 0 && nums[i] == nums[i - 1] && !visited[i - 1]) continue;
                visited[i] = true;
                List<List<Integer>> subs = permuteUniqueDfs(pos + 1);
                for (List<Integer> sub: subs) {
                    List<Integer> item = new ArrayList<>();
                    item.add(nums[i]);
                    item.addAll(sub);
                    res.add(item);
                visited[i] = false;
            }
        }

        return res;
    }
}
