class Solution {
    String s;
    Map<Integer, List<List<String>>> cache;
    public List<List<String>> partition(String s) {
        this.s = s;
        this.cache = new HashMap<>();
        return partitionDfs(0);
    }

    private List<List<String>>  partitionDfs(int start) {
        if(start == s.length()) {
            List<List<String>> res = new ArrayList<>();
            List<String> item = new ArrayList<>();
            res.add(item);
            return res;
        }

        if(cache.containsKey(start)){
            return cache.get(start);
        }

        List<List<String>> res = new ArrayList<>();
        for (int i = start + 1; i <= s.length(); i++) {
            String cur = s.substring(start, i);
            if(isValid(cur)) {
                List<List<String>> subs = partitionDfs(i);
                for (List<String> sub: subs) {
                    List<String> prefix_item = new ArrayList<>();
                    prefix_item.add(cur);
                    prefix_item.addAll(sub);
                    res.add(prefix_item);
                }
            }
        }
        cache.put(start, res);
        return res;
    }

    private boolean isValid(String str) {
        int l = 0;
        int r = str.length() - 1;    
        while(l < r) {
            if (str.charAt(l) != str.charAt(r)) return false;
            else {
                l++;
                r--;
            }
        }
        return true;
    }
}
```


