        if(len == k) {
            List<List<Integer>> res = new ArrayList<>();
            List<Integer> item = new ArrayList<>();
            res.add(item);
            return res;
        }

        List<List<Integer>> res = new ArrayList<>();
        for (int i = start; i <= n; i++) {
            List<List<Integer>> subs = combineDfs(i + 1, len + 1);
            for (List<Integer> sub : subs) {
                List<Integer> item = new ArrayList<>();
                item.add(i);
                item.addAll(sub);
                res.add(item);
            }
        }
        return res;
    }
}
