class Solution {
    int[] candidates;
    Map<Pair<Integer, Integer>, List<List<Integer>>> cache = new HashMap<>();
    public List<List<Integer>> combinationSum(int[] candidates, int target) {
        this.candidates = candidates;
        int n = candidates.length;        
        return combinationSumDfs(target, 0);
    }

    private List<List<Integer>> combinationSumDfs(int target, int pos) {
        if(target < 0) return new ArrayList<>();

        if(this.cache.containsKey(new Pair(target, pos))) {
            return this.cache.get(new Pair(target, pos));
        }

        if(target == 0) {
            List<List<Integer>> res = new ArrayList<>();
            List<Integer> item = new ArrayList<>();
            res.add(item);

            Pair<Integer, Integer> pair = new Pair(target, pos);
            cache.put(pair, res);

            return res;
        }


        List<List<Integer>> res = new ArrayList<>();
        for (int i = pos; i < candidates.length; i++) {
            List<List<Integer>> subs = combinationSumDfs(target - candidates[i], i);
            for (List<Integer> sub : subs) {
                List<Integer> item = new ArrayList<>();
                item.add(candidates[i]);
                item.addAll(sub);
                res.add(item);
            }
        }

        Pair<Integer, Integer> pair = new Pair(target, pos);
        cache.put(pair, res);

        return res;
    } 
}
