        List<List<Integer>> res = new ArrayList<>();

        for (int i = start; i < nums.length; i++) {
            List<List<Integer>> subs = subsetsDfs(i + 1, len - 1);
            for (List<Integer> sub : subs) {
                List<Integer> item = new ArrayList<>();
                item.add(nums[i]);
                item.addAll(sub);
                res.add(item);
            }
        }

