// Approach 2 commonly used for permutation and combination problems when duplicate elements are involved: frequency Hashmap 
class Solution {

    public List<List<Integer>> combinationSum2(int[] nums, int target) {
        List<List<Integer>> ans = new ArrayList();
        Map<Integer, Integer> map = new HashMap<>();
        for (int num : nums) {
            map.put(num, map.getOrDefault(num, 0) + 1);
        }
        recurse(target, map, new ArrayList(), ans);
        return ans;
    }

    private void recurse(int target, Map<Integer, Integer> map, List<Integer> tempList, List<List<Integer>> ans) {
        if (target == 0) {
            ans.add(new ArrayList<>(tempList));
            return;
        }
        if (target < 0) return;

        for (Map.Entry<Integer, Integer> entry : map.entrySet()) {
            int num = entry.getKey();
            int count = entry.getValue();
            if (count <= 0 || (!tempList.isEmpty() && num < tempList.get(tempList.size() - 1))) continue; // Second condition of taking only increaing sequence (or a decreasing seq) is what turns a permuation into a combination

            tempList.add(num);
            map.put(num, count - 1);
            recurse(target - num, map, tempList, ans);
            tempList.remove(tempList.size() - 1);
            map.put(num, count);
        }
    }
}
