        for(int i=start;i<n;i++){
            if(i>start && arr[i] == arr[i-1]) continue; //avoid duplicates
            if(target - arr[i] < 0) continue;
            subList.add(arr[i]);
            solve(arr,n,target-arr[i],i+1,ansList,subList);
            subList.remove(subList.size()-1);
        }
    }
