class Solution {
public:
    int req_sum;
    vector<vector<int>> ans;  // We will return this as final ans
    int l ; //this i is the size required and size of our candidates array
    void helper(vector<int>& candidates,int index,int sum,vector<int>& v){
        if(sum==req_sum)ans.push_back(v); //We have our solution
        if(index>=candidates.size() || sum>req_sum)return; // Just to check overflow conditions
        for(int i=index;i<l;i++){
            if(i>index && candidates[i]==candidates[i-1])continue; //Avoiding Duplicates as array is sorted prev like [1,1,6,7] index C[0]==C[1] so we should leave this.
            v.push_back(candidates[i]);
            helper(candidates,i+1,sum+candidates[i],v); // Recursive Call with the next index

            v.pop_back();  // Backtracking simply if we return from above statement then we will pop_back as last inserted element must be in either of these conditions i>c.size() || sum>req_sum 
        }
    }
    vector<vector<int>> combinationSum2(vector<int>& candidates, int target) {
        req_sum=target;
        vector<int> v;
        l=candidates.size();
        sort(candidates.begin(),candidates.end());
        helper(candidates,0,0,v);
        return ans;
    }
};
