class Solution {
public:
    vector<int> combination;
    vector<vector<int>> finalAnswer;
    vector<vector<int>> combinationSum2(vector<int>& candidates, int target) {
        if(candidates.size()==0){return finalAnswer;}
        sort(candidates.begin(),candidates.end()); //[1,1,2,5,6,7,10] target = 8
        int currentIndex = 0;
        CheckForSum(candidates, target, currentIndex);
        return finalAnswer;
    }

    void CheckForSum(vector<int>& candidates, int target, int idx){
        if(target==0){
            if(isPresentIn2dArray(finalAnswer,combination)==true){
                return;
            }else{
                finalAnswer.push_back(combination);
            return;
            }


        }
        for(int i = idx; i<candidates.size();i++){
            if(target<candidates[i]) break;
            combination.push_back(candidates[i]);
            CheckForSum(candidates,target-candidates[i], i+1);
            combination.pop_back();
        } 
    }

    bool isPresentIn2dArray( vector<vector<int>>& finalAnswer, vector<int>& combination ){
        bool isPresent = find(finalAnswer.begin(), finalAnswer.end(), combination) != finalAnswer.end();
        return isPresent;
    }
};
