class Solution:
    def backtrack(self, candidates, remaining, idx=0, got=[]):
        if remaining < 0:
            return

        if idx == len(candidates):
            if remaining == 0:
                yield tuple(got)
            return

        got.append(candidates[idx])
        yield from self.backtrack(candidates, remaining - got[-1], idx + 1)
        got.pop()
        yield from self.backtrack(candidates, remaining, idx + 1)


    def combinationSum2(self, candidates: List[int], target: int) -> List[List[int]]:
        candidates.sort()
        return set(self.backtrack(candidates, target))
```

