    public List<List<Integer>> combinationSum2(int[] candidates, int target) {
        Arrays.sort(candidates);
         List<List<Integer>> result = new ArrayList<>(); 
        combinationSumHelper(candidates, target, 0, result, new LinkedList<>());
        return result;
