        public void combinationSumHelper(int[] candidates, int target, int index, List<List<Integer>> result, LinkedList<Integer> temp){
       //if you have met the target the add result from temp     
       if( target == 0){
           result.add(new ArrayList<>(temp));
           return;
       } 

       //coundlnt find the target and exit     
       if(index >= candidates.length){
           return;
       }  

        //case 1: you include the number. for bounded knapsack u can include only once.
            //hence increment the index
       if(candidates[index] <= target){
           temp.add(candidates[index]);
           combinationSumHelper(candidates, target-candidates[index], index+1, result, temp);
           temp.removeLast();
       }  

       //skip duplicate starting points     
       while(index+1 < candidates.length && candidates[index]==candidates[index+1]) {
           index++;     
       }

    //case 2: you  dont include the number. same for bounded and unbounded knapsack
       combinationSumHelper(candidates,target, index+1, result,temp ); 
    }
}
